case "deviceCreated":
	var device = {properties: msg.payload.properties, isOn: true};
	//set properties input types
	device.propertiesInput = {};
	for(var k in device.properties){
		var prpType = typeof(device.properties[k]);
		var inputType = (prpType === "number") ? "number": (prpType === "boolean") ? "checkbox" : "text";
		device.propertiesInput[k] = inputType;
	}
	simulation[msg.payload.deviceType].devices[msg.payload.deviceId] = device;
	break;
case "deviceDeleted":
	var device = msg.payload;
	if(simulation[device.deviceType].devices[device.deviceId]){
		simulation[device.deviceType].devices[device.deviceId].isOn = false;
	}
	else
		return null; //device already deleted
	break;
case "propertyChange":
	var device = msg.payload;
	var changedProperty = device.changedProperty;
	simulation[device.deviceType].devices[device.deviceId].properties[changedProperty.name] = changedProperty.newValue;
	break;
default:
	return null;
}
flow.set("simulation", simulation);
msg.payload = simulation;
return msg;

